mapper.update:
update board_tbl set
	board_title=#{boardTitle}, board_thumb = #{boardThumb}, board_content = #{boardContent}
where board_no = #{boardNo}

mapper.select:
select * from board_file_tbl
where board_file_no in

	#{boardFileNo}

mapper.delete:
delete from board_file_tbl
where board_file_no in

	#{boardFileNo}

mapper.update:
update board_tbl set
	board_status = #{boardStatus}
where board_no = #{boardNo}
